sys_variable_value.value:
(function execute(inputs, outputs) {

  try{
  	var frmtOutput = 'Services and status<br/>';
  	var rawInput = inputs.result;

    // Format the output to be used in work notes and be in courier font

    //frmtOutput += rawInput.replace(/(\r\n|\n|\r)/gm,"<br/>").replace(/ {1,}/g," ");
  	frmtOutput += rawInput.replace(/(\r\n|\n|\r)/gm,"<br/>");
  	var formatted1 = '[code]<pre><code>' + frmtOutput + '</code></pre>[/code]';
  	outputs.formatresult = formatted1;
  } catch (e) {
    gs.info('Error formatting output: ' + e);
  }

})(inputs, outputs);

